  <script>
        const economicCalendarObj = {
            economicWidgetClass: "economic-calendar-widget",
            timeFormat: "HH:mm",
            dateFormat: "dd/MM/yyyy HH:mm",
            expiryDate: "",
            dateTypeTexts: {
                yesterday: "Yesterday",
