    <script>
        window.addEventListener("DOMContentLoaded", function() {
            function loadSection(url, targetId) {
                fetch(url)
                    .then(response => {
                        if (!response.ok) {
                            throw new Error('Network response was not ok');
                        }
                        return response.text();
                    })
                    .then(html => {
                        const container = document.getElementById(targetId);
                        container.innerHTML = html;
                        const scripts = container.getElementsByTagName('script');
                          for (let script of scripts) {
                            const newScript = document.createElement('script');
                            if (script.src) {
                              newScript.src = script.src; // External script
                            } else {
                              newScript.textContent = script.textContent; // Inline script
                            }
                            newScript.async = true;
                            newScript.id = targetId + '-script';
                            document.body.appendChild(newScript);
                            //document.body.removeChild(newScript); // Optional: Clean up after execution
                          }
                    })
                    .catch(error => {
                        console.error('Error:', error);
                        //document.getElementById(targetId).innerHTML = 'Error loading content. Please try again later.';
                    });
            }

