window.addEventListener('load', function () {
            // Tabs to mobile menu
            const mediaQuery = window.matchMedia("(max-width: " + window.getComputedStyle(document.documentElement).getPropertyValue('--bs-breakpoint-md') + ")"); // medium
            let menuCollapse = null;

            function collapsedMenuHandler(mq) { // create menu with BS Collapse
                    if (mq.matches) {
                        menuCollapse = bootstrap.Collapse.getOrCreateInstance(document.getElementById("collapseMenu"), { toggle:false });
                        collapseButtonContent();
                    }
            }
            function collapseButtonContent(){ // copy selection text to menu trigger
                document.querySelector(".nav-toggle").innerHTML = document.querySelector(".articles-tabs-menu .nav-link.active").innerHTML;
            }
        
            document.querySelector("#articles-tab").addEventListener("hidden.bs.tab", (e) => {  // Tab action to menu collapse
                if (mediaQuery.matches) {
                    if (e.target.classList.contains("nav-link") && menuCollapse) {
                        collapseButtonContent();
                        menuCollapse.hide();
                    }
                }
            });
            
            // Dropdown Filter
            document.querySelectorAll("#articles-tabContent .dropdown-item").forEach(item => item.addEventListener('click', () => filterItemClick(item)));
            function filterItemClick(clickedItem) { 
                const tab = clickedItem.closest(".articles-tabs-tab"),
                      dropToggle = tab.querySelector(".dropdown-toggle"),
                      dropItems = tab.querySelectorAll(".dropdown-item"),
                      articles = tab.querySelectorAll(".articles-tabs-list-item"),
                      clickedCategory = clickedItem.dataset.filter;
                dropToggle.innerText = clickedItem.innerText;
                dropItems.forEach(item => item.classList.remove("active"));
                clickedItem.classList.add("active");
                articles.forEach(item => {
                    if (item.dataset.filter === clickedCategory) {
                        item.classList.remove("d-none");
                    }
                    else {
                        item.classList.add("d-none");
                    }
                });
            }

            //init
            mediaQuery.addListener(collapsedMenuHandler);
            collapsedMenuHandler(mediaQuery);
        });
</script>

